import React from 'react';
import { motion } from 'framer-motion';
import { ArrowRight, Cpu, Brain, Zap } from 'lucide-react';

const Hero = () => {
  return (
    <section className="hero">
      <div className="container">
        <motion.div 
          className="hero-content"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          <motion.h1 
            className="hero-title"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            AI Agent for Developers
          </motion.h1>

          <motion.div 
            className="hero-visual"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.6 }}
          >
            <div className="tablet-device">
              <div className="tablet-screen">
                <div className="screen-content">
                  <div className="screen-header">
                    <span className="breadcrumb">Llamaware {'>'}{' '}Dashboard</span>
                    <div className="status-indicator">
                      <div className="status-dot"></div>
                      <span>Online</span>
                    </div>
                  </div>
                  <div className="ai-metrics">
                    <div className="metric-row">
                      <div className="metric-item">
                        <Cpu size={16} />
                        <div className="metric-info">
                          <div className="metric-value">2.3s</div>
                          <div className="metric-label">Response Time</div>
                        </div>
                      </div>
                      <div className="metric-item">
                        <Brain size={16} />
                        <div className="metric-info">
                          <div className="metric-value">99.2%</div>
                          <div className="metric-label">Accuracy</div>
                        </div>
                      </div>
                    </div>
                    <div className="metric-row">
                      <div className="metric-item">
                        <Zap size={16} />
                        <div className="metric-info">
                          <div className="metric-value">1.2M</div>
                          <div className="metric-label">Tokens Processed</div>
                        </div>
                      </div>
                      <div className="metric-item">
                        <div className="metric-info">
                          <div className="metric-value">24/7</div>
                          <div className="metric-label">Uptime</div>
                        </div>
                      </div>
                    </div>
                  </div>
                  <div className="command-line">
                    <div className="prompt">$</div>
                    <div className="command">llamaware --task "analyze codebase"</div>
                  </div>
                  <div className="response-preview">
                    <div className="response-line">Analyzing project structure...</div>
                    <div className="response-line">Found 15 files, 3 dependencies</div>
                    <div className="response-line">✅ Code quality: Excellent</div>
                  </div>
                </div>
              </div>
            </div>
          </motion.div>

          <motion.div 
            className="hero-actions"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.8 }}
          >
            <a href="#features" className="btn btn-primary">
              Get Started
              <ArrowRight size={16} />
            </a>
          </motion.div>
        </motion.div>
      </div>

      <style jsx>{`
        .hero {
          min-height: 100vh;
          display: flex;
          align-items: center;
          position: relative;
          background: #ffffff;
          text-align: center;
          padding: 120px 0;
        }

        .hero-content {
          max-width: 1000px;
          margin: 0 auto;
        }

        .hero-title {
          font-family: 'Georgia', 'Times New Roman', serif;
          font-size: 4.5rem;
          font-weight: 400;
          color: #1a202c;
          margin-bottom: 80px;
          line-height: 1.05;
          letter-spacing: -0.025em;
        }

        .hero-visual {
          margin-bottom: 60px;
          display: flex;
          justify-content: center;
        }

        .tablet-device {
          width: 400px;
          height: 280px;
          background: #000000;
          border-radius: 20px;
          padding: 8px;
          box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
          transform: rotate(-5deg);
        }

        .tablet-screen {
          width: 100%;
          height: 100%;
          background: linear-gradient(135deg, #1a202c 0%, #2d3748 100%);
          border-radius: 12px;
          padding: 20px;
          position: relative;
          overflow: hidden;
        }

        .screen-content {
          height: 100%;
          display: flex;
          flex-direction: column;
          justify-content: space-between;
        }

        .screen-header {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 20px;
        }

        .breadcrumb {
          font-size: 0.7rem;
          color: rgba(255, 255, 255, 0.7);
          font-weight: 400;
        }

        .status-indicator {
          display: flex;
          align-items: center;
          gap: 6px;
          background: rgba(56, 161, 105, 0.2);
          padding: 4px 8px;
          border-radius: 12px;
          font-size: 0.6rem;
          color: #38a169;
          font-weight: 500;
        }

        .status-dot {
          width: 6px;
          height: 6px;
          background: #38a169;
          border-radius: 50%;
          animation: pulse 2s infinite;
        }

        .ai-metrics {
          display: flex;
          flex-direction: column;
          gap: 12px;
          margin-bottom: 20px;
        }

        .metric-row {
          display: flex;
          justify-content: space-between;
          gap: 12px;
        }

        .metric-item {
          display: flex;
          align-items: center;
          gap: 8px;
          background: rgba(255, 255, 255, 0.1);
          padding: 8px 12px;
          border-radius: 8px;
          flex: 1;
        }

        .metric-item svg {
          color: #38a169;
        }

        .metric-info {
          display: flex;
          flex-direction: column;
        }

        .metric-value {
          font-size: 0.8rem;
          font-weight: 600;
          color: rgba(255, 255, 255, 0.9);
          line-height: 1;
        }

        .metric-label {
          font-size: 0.6rem;
          color: rgba(255, 255, 255, 0.6);
          font-weight: 400;
        }

        .command-line {
          display: flex;
          align-items: center;
          gap: 8px;
          margin-bottom: 12px;
          font-family: 'JetBrains Mono', monospace;
        }

        .prompt {
          color: #38a169;
          font-size: 0.8rem;
          font-weight: 600;
        }

        .command {
          color: rgba(255, 255, 255, 0.9);
          font-size: 0.8rem;
          font-weight: 400;
        }

        .response-preview {
          display: flex;
          flex-direction: column;
          gap: 4px;
        }

        .response-line {
          font-size: 0.7rem;
          color: rgba(255, 255, 255, 0.8);
          font-family: 'JetBrains Mono', monospace;
          font-weight: 400;
        }

        .response-line:last-child {
          color: #38a169;
        }

        .hero-actions {
          display: flex;
          justify-content: center;
        }

        @keyframes pulse {
          0%, 100% {
            opacity: 1;
          }
          50% {
            opacity: 0.5;
          }
        }

        @media (max-width: 768px) {
          .hero {
            padding: 80px 0;
          }

          .hero-title {
            font-size: 3rem;
            margin-bottom: 60px;
          }

          .tablet-device {
            width: 300px;
            height: 210px;
          }

          .metric-value {
            font-size: 0.7rem;
          }
        }

        @media (max-width: 480px) {
          .hero-title {
            font-size: 2.5rem;
            margin-bottom: 40px;
          }

          .tablet-device {
            width: 250px;
