import React from 'react';
import { motion } from 'framer-motion';
import { Github, Twitter, Mail, Heart, Star, GitBranch } from 'lucide-react';

const Footer = () => {
  const links = {
    project: [
      { name: 'Documentation', href: '#' },
      { name: 'API Reference', href: '#' },
      { name: 'Examples', href: '#' },
      { name: 'Changelog', href: '#' }
    ],
    community: [
      { name: 'GitHub Issues', href: 'https://github.com/bniladridas/Llamaware/issues' },
      { name: 'Discussions', href: 'https://github.com/bniladridas/Llamaware/discussions' },
      { name: 'Contributing', href: 'https://github.com/bniladridas/Llamaware/blob/main/CONTRIBUTING.md' },
      { name: 'Code of Conduct', href: '#' }
    ],
    resources: [
      { name: 'Blog', href: '#' },
      { name: 'Tutorials', href: '#' },
      { name: 'Best Practices', href: '#' },
      { name: 'FAQ', href: '#' }
    ]
  };

  const stats = [
    { label: 'GitHub Stars', value: '2.1k', icon: <Star size={16} /> },
    { label: 'Contributors', value: '12', icon: <GitBranch size={16} /> },
    { label: 'Downloads', value: '15k', icon: <Github size={16} /> }
  ];

  return (
    <footer className="footer">
      <div className="container">
        <div className="footer-content">
          <motion.div
            className="footer-brand"
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
          >
            <div className="brand-logo">
              <div className="logo-text">INGEN</div>
              <div className="logo-subtitle">Llamaware Agent</div>
            </div>
            <p className="brand-description">
              Professional AI agent built with modern C++ for developers who demand 
              performance, security, and reliability.
            </p>
            <div className="social-links">
              <a href="https://github.com/bniladridas/Llamaware" className="social-link">
                <Github size={20} />
              </a>
              <a href="#" className="social-link">
                <Twitter size={20} />
              </a>
              <a href="mailto:contact@llamaware.dev" className="social-link">
                <Mail size={20} />
              </a>
            </div>
          </motion.div>

          <div className="footer-links">
            {Object.entries(links).map(([category, categoryLinks], index) => (
              <motion.div
                key={category}
                className="link-column"
                initial={{ opacity: 0, y: 30 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: index * 0.1 }}
                viewport={{ once: true }}
              >
                <h3 className="link-title">{category.charAt(0).toUpperCase() + category.slice(1)}</h3>
                <ul className="link-list">
                  {categoryLinks.map((link, linkIndex) => (
                    <li key={linkIndex}>
                      <a href={link.href} className="link-item">
                        {link.name}
                      </a>
                    </li>
                  ))}
                </ul>
              </motion.div>
            ))}
          </div>
        </div>

        <motion.div
          className="footer-stats"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          viewport={{ once: true }}
        >
          <div className="stats-grid">
            {stats.map((stat, index) => (
              <div key={index} className="stat-item">
                <div className="stat-icon">{stat.icon}</div>
                <div className="stat-content">
                  <div className="stat-value">{stat.value}</div>
                  <div className="stat-label">{stat.label}</div>
                </div>
              </div>
            ))}
          </div>
        </motion.div>

        <motion.div
          className="footer-bottom"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.6 }}
          viewport={{ once: true }}
        >
          <div className="footer-divider"></div>
          <div className="footer-info">
            <p className="copyright">
              © 2024 Llamaware. Built with <Heart size={14} className="heart-icon" /> by developers.
            </p>
            <div className="footer-legal">
              <a href="#" className="legal-link">Privacy Policy</a>
              <a href="#" className="legal-link">Terms of Service</a>
              <a href="#" className="legal-link">License</a>
            </div>
          </div>
        </motion.div>
