import React from 'react';
import { motion } from 'framer-motion';

const Navigation = () => {
  return (
    <nav className="navigation">
      <div className="container">
        <motion.div 
          className="nav-pill"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
        >
          <a href="#features" className="nav-link">Benefits</a>
          <a href="#architecture" className="nav-link">Specifications</a>
          <a href="#installation" className="nav-link">How-to</a>
          <a href="#contact" className="nav-link">Contact Us</a>
        </motion.div>
      </div>

      <style jsx>{`
        .navigation {
          position: fixed;
          top: 20px;
          left: 0;
          right: 0;
          z-index: 1000;
          display: flex;
          justify-content: center;
        }

        .nav-pill {
          display: flex;
          gap: 40px;
          align-items: center;
          background: rgba(255, 255, 255, 0.85);
          padding: 16px 32px;
          border-radius: 50px;
          backdrop-filter: blur(15px);
          border: 1px solid rgba(255, 255, 255, 0.3);
          box-shadow: 0 8px 32px rgba(0, 0, 0, 0.1);
          max-width: 500px;
          width: 100%;
          justify-content: center;
        }

        .nav-link {
          color: #1a202c;
          text-decoration: none;
          font-size: 0.95rem;
          font-weight: 600;
          transition: color 0.2s ease;
          letter-spacing: -0.01em;
        }

        .nav-link:hover {
          color: #38a169;
        }

        @media (max-width: 768px) {
          .nav-pill {
            gap: 24px;
            padding: 14px 24px;
            margin: 0 20px;
          }

          .nav-link {
            font-size: 0.875rem;
          }
        }

        @media (max-width: 480px) {
          .nav-pill {
            gap: 16px;
            padding: 12px 20px;
          }

          .nav-link {
            font-size: 0.8rem;
          }
        }
      `}</style>
    </nav>
  );
};

export default Navigation;
