import React from 'react';
import { motion } from 'framer-motion';

const Navigation = () => {
  return (
    <nav className="navigation">
      <div className="nav-background"></div>
      <div className="container">
        <div className="nav-content">
          <motion.div 
            className="nav-brand"
            initial={{ opacity: 0, x: -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6 }}
          >
            <span className="brand-text">Llamaware</span>
          </motion.div>

          <motion.div 
            className="nav-links"
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.6, delay: 0.2 }}
          >
            <a href="#features" className="nav-link">Benefits</a>
            <a href="#architecture" className="nav-link">Specifications</a>
            <a href="#installation" className="nav-link">How-to</a>
            <a href="#contact" className="nav-link">Contact Us</a>
          </motion.div>
        </div>
      </div>

      <style jsx>{`
        .navigation {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          z-index: 1000;
          padding: 20px 0;
        }

        .nav-background {
          position: absolute;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: linear-gradient(135deg, rgba(255, 255, 255, 0.9) 0%, rgba(255, 255, 255, 0.7) 100%);
          backdrop-filter: blur(20px);
          border-bottom: 1px solid rgba(255, 255, 255, 0.2);
        }

        .nav-content {
          position: relative;
          z-index: 1;
          display: flex;
          justify-content: space-between;
          align-items: center;
          max-width: 1200px;
          margin: 0 auto;
          padding: 0 24px;
        }

        .nav-brand {
          display: flex;
          align-items: center;
        }

        .brand-text {
          font-size: 1.25rem;
          font-weight: 600;
          color: #1a202c;
          letter-spacing: -0.01em;
        }

        .nav-links {
          display: flex;
          gap: 40px;
          align-items: center;
          background: rgba(255, 255, 255, 0.8);
          padding: 12px 24px;
          border-radius: 50px;
          backdrop-filter: blur(10px);
          border: 1px solid rgba(255, 255, 255, 0.3);
          box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
        }

        .nav-link {
          color: #1a202c;
          text-decoration: none;
          font-size: 0.95rem;
          font-weight: 500;
          transition: color 0.2s ease;
        }

        .nav-link:hover {
          color: #38a169;
        }

        @media (max-width: 768px) {
          .nav-links {
            gap: 24px;
            padding: 10px 20px;
          }

          .nav-link {
            font-size: 0.875rem;
          }
        }

        @media (max-width: 480px) {
          .nav-links {
            gap: 16px;
            padding: 8px 16px;
          }

          .brand-text {
            font-size: 1.125rem;
          }
        }
      `}</style>
    </nav>
  );
};

export default Navigation;
