import React, { useEffect } from 'react';
import './App.css';
import Hero from './components/Hero';
import Features from './components/Features';
import Architecture from './components/Architecture';
import Installation from './components/Installation';
import Footer from './components/Footer';
import Navigation from './components/Navigation';

function App() {
  useEffect(() => {
    // Add heavy scroll friction
    let isScrolling = false;
    let scrollTimeout;
    let lastScrollTop = 0;
    let scrollResistance = 0.3; // Heavy resistance (0.1 = light, 0.9 = very heavy)

    const handleScroll = (e) => {
      if (isScrolling) {
        e.preventDefault();
        return;
      }

      isScrolling = true;
      clearTimeout(scrollTimeout);

      const currentScrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const scrollDelta = currentScrollTop - lastScrollTop;

      // Apply resistance to scroll
      if (Math.abs(scrollDelta) > 1) {
        const resistedScroll = scrollDelta * scrollResistance;
        window.scrollTo(0, lastScrollTop + resistedScroll);
      }

      lastScrollTop = currentScrollTop;

      scrollTimeout = setTimeout(() => {
        isScrolling = false;
      }, 50); // Delay before allowing next scroll
    };

    // Add wheel event listener for more control
    const handleWheel = (e) => {
      e.preventDefault();

      const delta = e.deltaY;
      const currentScrollTop = window.pageYOffset || document.documentElement.scrollTop;
      const resistedDelta = delta * scrollResistance;

      window.scrollTo({
        top: currentScrollTop + resistedDelta,
        behavior: 'auto'
      });
    };

    // Add touch events for mobile
    let touchStartY = 0;
    let touchStartScrollTop = 0;

    const handleTouchStart = (e) => {
      touchStartY = e.touches[0].clientY;
      touchStartScrollTop = window.pageYOffset || document.documentElement.scrollTop;
    };

    const handleTouchMove = (e) => {
      e.preventDefault();

      const touchDeltaY = touchStartY - e.touches[0].clientY;
      const resistedDelta = touchDeltaY * scrollResistance;

      window.scrollTo({
        top: touchStartScrollTop + resistedDelta,
        behavior: 'auto'
      });
    };

    // Add event listeners
    window.addEventListener('scroll', handleScroll, { passive: false });
    window.addEventListener('wheel', handleWheel, { passive: false });
    window.addEventListener('touchstart', handleTouchStart, { passive: false });
    window.addEventListener('touchmove', handleTouchMove, { passive: false });

    // Cleanup
    return () => {
      window.removeEventListener('scroll', handleScroll);
      window.removeEventListener('wheel', handleWheel);
      window.removeEventListener('touchstart', handleTouchStart);
      window.removeEventListener('touchmove', handleTouchMove);
      clearTimeout(scrollTimeout);
    };
  }, []);

  return (
    <div className="App">
      <Navigation />
      <Hero />
      <Features />
      <Architecture />
      <Installation />
      <Footer />
    </div>
  );
}

export default App;
