import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Copy, Check, Terminal, Package, Container } from 'lucide-react';

const Installation = () => {
  const [copiedCommand, setCopiedCommand] = useState(null);
  const [activeTab, setActiveTab] = useState('quick');

  const copyToClipboard = (text, commandId) => {
    navigator.clipboard.writeText(text);
    setCopiedCommand(commandId);
    setTimeout(() => setCopiedCommand(null), 2000);
  };

  const installationMethods = {
    quick: {
      title: "Quick Start",
      icon: <Terminal size={20} />,
      steps: [
        {
          title: "Clone Repository",
          command: "git clone https://github.com/bniladridas/Llamaware.git",
          description: "Get the latest source code"
        },
        {
          title: "Build Project",
          command: "cd Llamaware && mkdir build && cd build && cmake .. && make",
          description: "Compile the agent"
        },
        {
          title: "Run Preflight",
          command: "make preflight",
          description: "Verify everything works"
        },
        {
          title: "Start Agent",
          command: "./build/bin/llamaware-agent",
          description: "Launch the AI agent"
        }
      ]
    },
    package: {
      title: "Package Managers",
      icon: <Package size={20} />,
      steps: [
        {
          title: "macOS (Homebrew)",
          command: "brew tap bniladridas/llamaware\nbrew install llamaware-agent",
          description: "Install via Homebrew"
        },
        {
          title: "Ubuntu/Debian",
          command: "wget https://github.com/bniladridas/Llamaware/releases/latest/download/llamaware-agent-linux-x64\nchmod +x llamaware-agent-linux-x64\nsudo mv llamaware-agent-linux-x64 /usr/local/bin/llamaware-agent",
          description: "Install binary directly"
        },
        {
          title: "Configuration",
          command: "cp .env.example .env\n# Edit .env with your API keys",
          description: "Setup environment"
        }
      ]
    },
    docker: {
      title: "Docker",
      icon: <Container size={20} />,
      steps: [
        {
          title: "Pull Image",
          command: "docker pull llamaware/agent:latest",
          description: "Get the Docker image"
        },
        {
          title: "Run Container",
          command: "docker run -it --rm \\\n  -e TOGETHER_API_KEY=your_key \\\n  -e SERPAPI_KEY=your_key \\\n  llamaware/agent:latest",
          description: "Start containerized agent"
        },
        {
          title: "Docker Compose",
          command: "curl -O https://raw.githubusercontent.com/bniladridas/Llamaware/main/package/docker/docker-compose.yml\ndocker-compose up",
          description: "Use with Ollama service"
        }
      ]
    }
  };

  const requirements = [
    {
      name: "C++17 Compiler",
      description: "GCC 7+, Clang 5+, or MSVC 2017+",
      status: "required"
    },
    {
      name: "CMake",
      description: "Version 3.14 or higher",
      status: "required"
    },
    {
      name: "Dependencies",
      description: "libcpr and nlohmann/json",
      status: "required"
    },
    {
      name: "Together API Key",
      description: "For online AI mode",
      status: "optional"
    },
    {
      name: "SerpAPI Key",
      description: "For web search functionality",
      status: "optional"
    },
    {
      name: "Ollama",
      description: "For offline AI mode",
      status: "optional"
    }
  ];

  return (
    <section id="installation" className="section">
      <div className="container">
        <motion.div
          initial={{ opacity: 0, y: 50 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2 className="section-title">Get Started</h2>
          <p className="section-subtitle">
            Multiple installation methods to fit your workflow and environment
          </p>
        </motion.div>

        <div className="installation-content">
          <div className="installation-tabs">
            {Object.entries(installationMethods).map(([key, method]) => (
              <button
                key={key}
                className={`tab-button ${activeTab === key ? 'active' : ''}`}
                onClick={() => setActiveTab(key)}
              >
                {method.icon}
                {method.title}
              </button>
            ))}
          </div>

          <motion.div
            className="installation-steps"
            key={activeTab}
            initial={{ opacity: 0, x: 20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
          >
            <h3 className="steps-title">{installationMethods[activeTab].title}</h3>
            <div className="steps-list">
              {installationMethods[activeTab].steps.map((step, index) => (
                <motion.div
                  key={index}
                  className="step-item"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                >
                  <div className="step-header">
                    <div className="step-number">{index + 1}</div>
                    <div className="step-info">
                      <h4 className="step-title">{step.title}</h4>
                      <p className="step-description">{step.description}</p>
                    </div>
                  </div>
                  <div className="step-command">
                    <div className="command-header">
                      <span className="command-label">Command</span>
                      <button
                        className="copy-button"
                        onClick={() => copyToClipboard(step.command, `${activeTab}-${index}`)}
                      >
                        {copiedCommand === `${activeTab}-${index}` ? (
                          <Check size={16} />
                        ) : (
                          <Copy size={16} />
                        )}
                      </button>
                    </div>
                    <pre className="command-text">{step.command}</pre>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>

          <motion.div
            className="requirements-section"
            initial={{ opacity: 0, y: 50 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.4 }}
            viewport={{ once: true }}
          >
            <h3 className="requirements-title">Requirements</h3>
            <div className="requirements-grid">
              {requirements.map((req, index) => (
                <div key={index} className="requirement-item">
                  <div className="requirement-header">
